  function DimColour(s, amt)
    if not s then return end
    local t = {}
    for i in s:gmatch("[%d%.]+") do 
      t[#t+1] = math.floor(tonumber(i)*amt)
    end
    return string.format('%i',t[1])..' '..string.format('%i',t[2])..' '..string.format('%i',t[3])
  end
  
