          end
          
        elseif gflag == 4 and not lvar.overtemplate_active then

          if template.layer and template.layer[lvar.overtemplate] then
            tmp = template.layer[lvar.overtemplate]
          else
            tmp = {pos = {}, dirty = {}}
          end
                  
        else
          --gflag = 1
          tmp = template
        end
      
