    --DBG(tn)
    if tr then
      for i = 0, reaper.CountTrackEnvelopes(tr)-1 do
  
        local env = reaper.GetTrackEnvelope(tr, i)
        if env then
          --Set index
          --reaper.GetSetEnvelopeInfo_String(env, 'P_EXT:LBX_ID', i, true)
          local _, chunk = reaper.GetEnvelopeStateChunk(env,'',false)
