          tab[i] = {id = idname..guid, i = i, trn = trn, env = env,
                   active = active,
                   visible = visible,
                   armed = armed,
                   inlane = inLane,
                   laneheight = laneHeight}
          local _, envname = reaper.GetEnvelopeName(env)
          
          --Hide envelope
          --if hide then
          --  VisEnv(env, false)
          --end
        end
      end
      
      --reaper.SetOnlyTrackSelected(tr)
