          lvar.lstfiltsel = {}
          for i = 1, #lvar.lstfilt do
            lvar.lstfiltsel[lvar.lstfilt[i]] = true
          end
          UpdateFilter()
        else
          lvar.userfilter.select = nil
        end
        lupd.update_gfx = true
      
