                                             or lvar.midieditor_cclanes_solo_assign) then
        else
          lvar.midieditor_cclanes_solo = not lvar.midieditor_cclanes_solo
        end

        lvar.midieditor_cclanes_solo_assign = nil
        lvar.midieditor_cclanes_solovel = false        
        
