  function inCubic(t)
    return t^3
  end
  
  function outCubic(t)
    if t < 0 then
      t = -t - 1
      return -(t^3 + 1)
    else
      t = t - 1
      return t^3 + 1
    end
  end
  
  function inSine(t)
    if t < 0 then
      return -(-1 * math.cos(-t * (pi / 2)) + 1)
    else
      return -1 * math.cos(t * (pi / 2)) + 1
    end
  end
  
  function outSine(t)
    return 1 * math.sin(t * (pi / 2))
  end
    
