    local ffn = paths.resource_path..LBX_LANESXTRA
    file=io.open(ffn,"w")
    file:write('[velshow]'..tostring(lvar.lanes_xtra.velshow)..'\n')
    file:write('[velbottom]'..tostring(lvar.lanes_xtra.velbottom)..'\n')
    
    for trnm, tab in pairs(lvar.lanes_xtra) do  
