              template = LoadFXParamTemplate(FFX)
              TemplateChanged()
            else
              lvar.tagfilt.selected = lvar.tagfilt[r].tag
              lvar.tagfilt.selectednum = r
              lupd.update_tags = true
            end
          else
            lvar.tagfilt.selected = nil
