      lvar.running = 0
      time_delay = reaper.time_precise() + lvar.inittime[lvar.initstage]      
      lvar.initstage = 2
    elseif lvar.initstage == 2 then

      if rt > time_delay then

        ResendHandshake()
        SendSetUpSYSX()
        
        --Read settings
        --ResetFSet()
        GetFFX(true)
      
        time_delay = reaper.time_precise() + lvar.inittime[lvar.initstage]
