  function GetCtlMaps(sub)
    
    local i = 0
    local ii = 1
    local tmp = {}
    local mapf = reaper.EnumerateFiles(paths.ctemplate_path,i)
    while mapf ~= nil do
      local mapn, mape = string.match(mapf,'(.+)%.(.*)') 
      if mape == 'skctlmap' then
        tmp[ii] = mapn
        ii = ii + 1
      end
      i=i+1
      mapf = reaper.EnumerateFiles(paths.ctemplate_path,i)
    end
    
    ii=ii-1
    local str = ''
    if sub then
      str = '|>Controller Maps'
    end
    for i = 1, ii do
      if i == ii and sub then
        str = str .. '|<' .. tmp[i]      
      else
        str = str .. '|' .. tmp[i]
      end
    end
    if not sub then
      str = string.sub(str,2)
    end
    return tmp, str, ii
  end
  
