          lvar.env_tralign = 0
        else
          lvar.env_tralign = 1
        end
      elseif res == 10+mode_offset then
        if lvar.env_tralign == 2 then
          lvar.env_tralign = 0
        else
          lvar.env_tralign = 2
        end
      end
    end    
  
